import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  TextField,
  Button,
  Switch,
  FormControlLabel,
  Alert,
  CircularProgress,
} from '@mui/material';
import { Save as SaveIcon } from '@mui/icons-material';
import api from '../services/api';

interface Parametros {
  id: number;
  costoFijoPorLitro: number;
  produccionMaximaTotal: number;
  penalizacionProduccionExtra: number;
  permitirProduccionExtra: boolean;
}

const ParametrosPage: React.FC = () => {
  const [parametros, setParametros] = useState<Parametros | null>(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string>('');
  const [success, setSuccess] = useState<string>('');

  const [formData, setFormData] = useState({
    costoFijoPorLitro: 0.5,
    produccionMaximaTotal: 1000000,
    penalizacionProduccionExtra: 1.5,
    permitirProduccionExtra: true,
  });

  useEffect(() => {
    loadParametros();
  }, []);

  const loadParametros = async () => {
    try {
      setLoading(true);
      const response = await api.get('/parametros');
      if (response.data) {
        setParametros(response.data);
        setFormData({
          costoFijoPorLitro: response.data.costoFijoPorLitro,
          produccionMaximaTotal: response.data.produccionMaximaTotal,
          penalizacionProduccionExtra: response.data.penalizacionProduccionExtra,
          permitirProduccionExtra: response.data.permitirProduccionExtra,
        });
      }
    } catch (error) {
      console.error('Error loading parameters:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    try {
      setSaving(true);
      setError('');
      setSuccess('');

      await api.patch('/parametros', formData);
      setSuccess('Parámetros actualizados exitosamente');
      await loadParametros();
    } catch (error: any) {
      setError(error.response?.data?.message || 'Error al guardar los parámetros');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        Parámetros del Modelo de Optimización
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
          {success}
        </Alert>
      )}

      <Card>
        <CardContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
            <TextField
              label="Costo Fijo por Litro Producido (USD)"
              type="number"
              value={formData.costoFijoPorLitro}
              onChange={(e) => setFormData({ ...formData, costoFijoPorLitro: parseFloat(e.target.value) })}
              fullWidth
              inputProps={{ min: 0, step: 0.01 }}
              helperText="Costo adicional por litro además del costo del aceite crudo"
            />

            <TextField
              label="Producción Máxima Total (L)"
              type="number"
              value={formData.produccionMaximaTotal}
              onChange={(e) => setFormData({ ...formData, produccionMaximaTotal: parseFloat(e.target.value) })}
              fullWidth
              inputProps={{ min: 0, step: 1 }}
              helperText="Cantidad máxima de litros que se pueden producir en total"
            />

            <TextField
              label="Penalización por Producción Extra"
              type="number"
              value={formData.penalizacionProduccionExtra}
              onChange={(e) => setFormData({ ...formData, penalizacionProduccionExtra: parseFloat(e.target.value) })}
              fullWidth
              inputProps={{ min: 0, step: 0.01 }}
              helperText="Factor multiplicador para penalizar la producción excedente"
            />

            <FormControlLabel
              control={
                <Switch
                  checked={formData.permitirProduccionExtra}
                  onChange={(e) => setFormData({ ...formData, permitirProduccionExtra: e.target.checked })}
                />
              }
              label="Permitir producción extra a la demanda"
            />

            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
              <Button
                variant="contained"
                startIcon={<SaveIcon />}
                onClick={handleSave}
                disabled={saving}
              >
                {saving ? 'Guardando...' : 'Guardar Parámetros'}
              </Button>
            </Box>
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
};

export default ParametrosPage;
