import React, { useState, useEffect } from 'react';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  Box,
  Button,
  Alert,
  CircularProgress,
} from '@mui/material';
import {
  OilBarrel as OilBarrelIcon,
  LocalGasStation as GasStationIcon,
  TrendingUp as TrendingUpIcon,
  Warning as WarningIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { aceitesService } from '../services/aceitesService';
import { gasolinasService } from '../services/gasolinasService';
import { optimizacionService } from '../services/optimizacionService';

interface DashboardStats {
  totalAceites: number;
  totalGasolinas: number;
  totalCapacity: number;
  totalDemand: number;
  averageCost: number;
  averagePrice: number;
}

const Dashboard: React.FC = () => {
  const navigate = useNavigate();
  const [stats, setStats] = useState<DashboardStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [validationErrors, setValidationErrors] = useState<string[]>([]);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      const [aceites, gasolinas, aceitesStats, gasolinasStats, validation] = await Promise.all([
        aceitesService.getAll(),
        gasolinasService.getAll(),
        aceitesService.getStats(),
        gasolinasService.getStats(),
        optimizacionService.validarDatos(),
      ]);

      setStats({
        totalAceites: aceites.length,
        totalGasolinas: gasolinas.length,
        totalCapacity: aceitesStats.totalCapacity,
        totalDemand: gasolinasStats.totalDemand,
        averageCost: aceitesStats.averageCost,
        averagePrice: gasolinasStats.averagePrice,
      });

      if (!validation.valido) {
        setValidationErrors(validation.errores);
      }
    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const StatCard: React.FC<{
    title: string;
    value: string | number;
    icon: React.ReactNode;
    color: string;
  }> = ({ title, value, icon, color }) => (
    <Card sx={{ height: '100%' }}>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Box sx={{ color, mr: 1 }}>{icon}</Box>
          <Typography variant="h6" component="div">
            {title}
          </Typography>
        </Box>
        <Typography variant="h4" component="div" sx={{ fontWeight: 'bold', color }}>
          {value}
        </Typography>
      </CardContent>
    </Card>
  );

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        Dashboard - EnergíaFina S.A.
      </Typography>

      {validationErrors.length > 0 && (
        <Alert severity="warning" sx={{ mb: 3 }}>
          <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 1 }}>
            Problemas detectados en la configuración:
          </Typography>
          <ul style={{ margin: 0, paddingLeft: 20 }}>
            {validationErrors.map((error, index) => (
              <li key={index}>{error}</li>
            ))}
          </ul>
        </Alert>
      )}

      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Aceites Crudos"
            value={stats?.totalAceites || 0}
            icon={<OilBarrelIcon />}
            color="#1976d2"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Tipos de Gasolina"
            value={stats?.totalGasolinas || 0}
            icon={<GasStationIcon />}
            color="#2e7d32"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Capacidad Total (L)"
            value={stats?.totalCapacity.toLocaleString() || 0}
            icon={<OilBarrelIcon />}
            color="#ed6c02"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Demanda Total (L)"
            value={stats?.totalDemand.toLocaleString() || 0}
            icon={<GasStationIcon />}
            color="#9c27b0"
          />
        </Grid>
      </Grid>

      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Costo Promedio (USD/L)"
            value={`$${stats?.averageCost.toFixed(2) || '0.00'}`}
            icon={<TrendingUpIcon />}
            color="#d32f2f"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Precio Promedio (USD/L)"
            value={`$${stats?.averagePrice.toFixed(2) || '0.00'}`}
            icon={<TrendingUpIcon />}
            color="#388e3c"
          />
        </Grid>
      </Grid>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Acciones Rápidas
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                <Button
                  variant="contained"
                  startIcon={<OilBarrelIcon />}
                  onClick={() => navigate('/aceites')}
                  fullWidth
                >
                  Gestionar Aceites Crudos
                </Button>
                <Button
                  variant="contained"
                  startIcon={<GasStationIcon />}
                  onClick={() => navigate('/gasolinas')}
                  fullWidth
                >
                  Gestionar Tipos de Gasolina
                </Button>
                <Button
                  variant="contained"
                  startIcon={<TrendingUpIcon />}
                  onClick={() => navigate('/optimizacion')}
                  fullWidth
                  disabled={validationErrors.length > 0}
                >
                  Ejecutar Optimización
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Estado del Sistema
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Box
                    sx={{
                      width: 12,
                      height: 12,
                      borderRadius: '50%',
                      backgroundColor: validationErrors.length === 0 ? '#4caf50' : '#f44336',
                    }}
                  />
                  <Typography>
                    {validationErrors.length === 0
                      ? 'Sistema listo para optimización'
                      : 'Problemas de configuración detectados'}
                  </Typography>
                </Box>
                {stats && (
                  <>
                    <Typography variant="body2" color="text.secondary">
                      Capacidad disponible: {stats.totalCapacity.toLocaleString()} L
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Demanda del mercado: {stats.totalDemand.toLocaleString()} L
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Margen promedio: ${(stats.averagePrice - stats.averageCost).toFixed(2)}/L
                    </Typography>
                  </>
                )}
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
