import React, { useState } from 'react';
import {
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Alert,
  CircularProgress,
  Chip,
  Divider,
  Tabs,
  Tab,
} from '@mui/material';
import {
  PlayArrow as PlayArrowIcon,
  TrendingUp as TrendingUpIcon,
  AttachMoney as MoneyIcon,
  Warning as WarningIcon,
  Cloud as CloudIcon,
  Psychology as PsychologyIcon,
  TableChart as TableChartIcon,
} from '@mui/icons-material';
import { optimizacionService, OptimizacionResponse } from '../services/optimizacionService';

const OptimizacionPage: React.FC = () => {
  const [resultado, setResultado] = useState<OptimizacionResponse | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string>('');
  const [tab, setTab] = useState(0);
  const [watsonResultados, setWatsonResultados] = useState<any | null>(null);
  const [loadingWatson, setLoadingWatson] = useState(false);
  const [errorWatson, setErrorWatson] = useState<string>('');

  // Ejecuta la optimización simulada/local (legacy)
  const ejecutarOptimizacion = async () => {
    try {
      setLoading(true);
      setError('');
      const data = await optimizacionService.ejecutarOptimizacion();
      setResultado(data);
    } catch (error: any) {
      setError(error.response?.data?.message || 'Error al ejecutar la optimización');
    } finally {
      setLoading(false);
    }
  };

  // Ejecuta la optimización real en Watson Studio y obtiene los resultados reales
  const ejecutarWatson = async () => {
    try {
      setLoadingWatson(true);
      setErrorWatson('');
      setWatsonResultados(null);
      const data = await optimizacionService.getResultadosWatson();
      setWatsonResultados(data);
    } catch (error: any) {
      setErrorWatson(error.response?.data?.message || 'Error al obtener resultados de Watson Studio');
    } finally {
      setLoadingWatson(false);
    }
  };

  const MetricCard: React.FC<{
    title: string;
    value: string;
    icon: React.ReactNode;
    color: string;
  }> = ({ title, value, icon, color }) => (
    <Card>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Box sx={{ color, mr: 1 }}>{icon}</Box>
          <Typography variant="h6" component="div">
            {title}
          </Typography>
        </Box>
        <Typography variant="h4" component="div" sx={{ fontWeight: 'bold', color }}>
          {value}
        </Typography>
      </CardContent>
    </Card>
  );

  // Componente para mostrar una tabla genérica a partir de un array de objetos
  const TablaGenerica: React.FC<{ titulo: string; data: any[] }> = ({ titulo, data }) => (
    <Card sx={{ mb: 4 }}>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          {titulo}
        </Typography>
        <TableContainer component={Paper}>
          <Table size="small">
            <TableHead>
              <TableRow>
                {data.length > 0 && Object.keys(data[0]).map((col) => (
                  <TableCell key={col}>{col}</TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {data.map((row, idx) => (
                <TableRow key={idx}>
                  {Object.values(row).map((val, i) => (
                    <TableCell key={i}>{String(val)}</TableCell>
                  ))}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </CardContent>
    </Card>
  );

  return (
    <Box>
      {/* Header con información del motor de optimización */}
      <Card sx={{ mb: 3, bgcolor: '#f8f9fa' }}>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
            <CloudIcon sx={{ color: '#1976d2', mr: 1 }} />
            <Typography variant="h5" component="h1" sx={{ fontWeight: 'bold' }}>
              Optimización de Producción con IBM Watson Studio
            </Typography>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <PsychologyIcon sx={{ color: '#388e3c', mr: 1, fontSize: 20 }} />
            <Typography variant="body1" color="text.secondary">
              Motor de optimización: <strong>Modelo OPL (Optimization Programming Language)</strong>
            </Typography>
          </Box>
          <Typography variant="body2" color="text.secondary">
            La optimización se ejecuta en IBM Watson Studio utilizando programación lineal para maximizar 
            el beneficio neto considerando restricciones de capacidad, calidad y demanda.
          </Typography>
        </CardContent>
      </Card>

      {/* Tabs para alternar entre simulación y resultados reales */}
      <Tabs value={tab} onChange={(_, v) => setTab(v)} sx={{ mb: 3 }}>
        <Tab label="Simulación Backend" icon={<PlayArrowIcon />} iconPosition="start" />
        <Tab label="Resultados Reales Watson Studio" icon={<TableChartIcon />} iconPosition="start" />
      </Tabs>

      {/* Tab 0: Simulación local/backend */}
      {tab === 0 && (
        <Box>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
            <Typography variant="h4" component="h2">
              Ejecutar Optimización (Simulación Backend)
            </Typography>
            <Button
              variant="contained"
              startIcon={<PlayArrowIcon />}
              onClick={ejecutarOptimizacion}
              disabled={loading}
              size="large"
              sx={{ 
                bgcolor: '#1976d2',
                '&:hover': { bgcolor: '#1565c0' }
              }}
            >
              {loading ? 'Ejecutando...' : 'Ejecutar Optimización'}
            </Button>
          </Box>

          {error && (
            <Alert severity="error" sx={{ mb: 3 }} onClose={() => setError('')}>
              {error}
            </Alert>
          )}

          {loading && (
            <Box sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
              <CircularProgress size={60} sx={{ mb: 2 }} />
              <Typography variant="h6" color="text.secondary">
                Ejecutando optimización en backend...
              </Typography>
            </Box>
          )}

          {resultado && (
            <Box>
              {/* Información del resultado */}
              <Card sx={{ mb: 3, bgcolor: '#e8f5e8' }}>
                <CardContent>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    <PsychologyIcon sx={{ color: '#388e3c', mr: 1 }} />
                    <Typography variant="h6" color="#388e3c">
                      Optimización Completada Exitosamente
                    </Typography>
                  </Box>
                  <Typography variant="body2" color="text.secondary">
                    Resultados obtenidos del modelo OPL ejecutado en IBM Watson Studio
                  </Typography>
                </CardContent>
              </Card>

              {/* Métricas principales */}
              <Grid container spacing={3} sx={{ mb: 4 }}>
                <Grid item xs={12} sm={6} md={3}>
                  <MetricCard
                    title="Ingreso Total"
                    value={`$${resultado.metricas.ingresoTotal.toLocaleString()}`}
                    icon={<MoneyIcon />}
                    color="#2e7d32"
                  />
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                  <MetricCard
                    title="Costo Total"
                    value={`$${resultado.metricas.costoTotal.toLocaleString()}`}
                    icon={<MoneyIcon />}
                    color="#d32f2f"
                  />
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                  <MetricCard
                    title="Beneficio Neto"
                    value={`$${resultado.metricas.beneficioNeto.toLocaleString()}`}
                    icon={<TrendingUpIcon />}
                    color={resultado.metricas.beneficioNeto >= 0 ? '#1976d2' : '#f57c00'}
                  />
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                  <MetricCard
                    title="Excedentes"
                    value={`${resultado.metricas.litrosExcedentes.toLocaleString()} L`}
                    icon={<WarningIcon />}
                    color="#ed6c02"
                  />
                </Grid>
              </Grid>

              {/* Tabla de asignaciones */}
              <Card sx={{ mb: 4 }}>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Asignaciones Óptimas de Aceites a Gasolinas
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Resultado del modelo OPL: variables Blend[o][g]
                  </Typography>
                  <TableContainer component={Paper}>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Aceite</TableCell>
                          <TableCell>Gasolina</TableCell>
                          <TableCell align="right">Litros Asignados</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {resultado.asignaciones.map((asignacion, index) => (
                          <TableRow key={index}>
                            <TableCell>{asignacion.aceiteNombre}</TableCell>
                            <TableCell>{asignacion.gasolinaNombre}</TableCell>
                            <TableCell align="right">
                              {asignacion.litros.toLocaleString()} L
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </CardContent>
              </Card>

              {/* Tabla de producción */}
              <Card>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Producción por Tipo de Gasolina
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    Incluye variables a[g] para producción adicional según el modelo OPL
                  </Typography>
                  <TableContainer component={Paper}>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Gasolina</TableCell>
                          <TableCell align="right">Demanda (L)</TableCell>
                          <TableCell align="right">Producido (L)</TableCell>
                          <TableCell align="right">Excedente (L)</TableCell>
                          <TableCell align="right">Cumplimiento</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {resultado.produccion.map((prod) => (
                          <TableRow key={prod.gasolinaId}>
                            <TableCell>{prod.gasolinaNombre}</TableCell>
                            <TableCell align="right">{prod.demanda.toLocaleString()}</TableCell>
                            <TableCell align="right">{prod.litrosProducidos.toLocaleString()}</TableCell>
                            <TableCell align="right">
                              {prod.excedente > 0 ? (
                                <Chip
                                  label={`${prod.excedente.toLocaleString()} L`}
                                  color="warning"
                                  size="small"
                                />
                              ) : (
                                '-'
                              )}
                            </TableCell>
                            <TableCell align="right">
                              <Chip
                                label={`${prod.porcentajeCumplimiento.toFixed(1)}%`}
                                color={prod.porcentajeCumplimiento >= 100 ? 'success' : 'warning'}
                                size="small"
                              />
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </CardContent>
              </Card>
            </Box>
          )}
        </Box>
      )}

      {/* Tab 1: Resultados reales de Watson Studio */}
      {tab === 1 && (
        <Box>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
            <Typography variant="h4" component="h2">
              Ejecutar Optimización Real en Watson Studio
            </Typography>
            <Button
              variant="contained"
              startIcon={<CloudIcon />}
              onClick={ejecutarWatson}
              disabled={loadingWatson}
              size="large"
              sx={{ 
                bgcolor: '#388e3c',
                '&:hover': { bgcolor: '#256029' }
              }}
            >
              {loadingWatson ? 'Ejecutando en Watson Studio...' : 'Ejecutar en Watson Studio'}
            </Button>
          </Box>

          {errorWatson && (
            <Alert severity="error" sx={{ mb: 3 }} onClose={() => setErrorWatson('')}>
              {errorWatson}
            </Alert>
          )}

          {loadingWatson && (
            <Box sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
              <CircularProgress size={60} sx={{ mb: 2 }} />
              <Typography variant="h6" color="text.secondary">
                Ejecutando optimización real en Watson Studio...
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                Esto puede tardar unos minutos dependiendo del tamaño de los datos.
              </Typography>
            </Box>
          )}

          {/* Mostrar resultados reales */}
          {watsonResultados && (
            <Box>
              <Alert severity="success" sx={{ mb: 3 }}>
                Resultados reales obtenidos del modelo OPL ejecutado en Watson Studio.<br />
                <strong>Job ID:</strong> {watsonResultados.jobId} &nbsp;|&nbsp; <strong>Estado:</strong> {watsonResultados.status}
              </Alert>
              {/* Mostrar tabla resultadoMezclas */}
              <TablaGenerica titulo="resultadoMezclas" data={watsonResultados.resultadoMezclas} />
              {/* Mostrar tabla resultadoAdicionalGasolinas */}
              <TablaGenerica titulo="resultadoAdicionalGasolinas" data={watsonResultados.resultadoAdicionalGasolinas} />
            </Box>
          )}
        </Box>
      )}
    </Box>
  );
};

export default OptimizacionPage;
