import React, { useState } from 'react';
import {
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Alert,
  CircularProgress,
  Chip,
  Divider,
} from '@mui/material';
import {
  PlayArrow as PlayArrowIcon,
  TrendingUp as TrendingUpIcon,
  AttachMoney as MoneyIcon,
  Warning as WarningIcon,
  Cloud as CloudIcon,
  Psychology as PsychologyIcon,
} from '@mui/icons-material';
import { optimizacionService, OptimizacionResponse } from '../services/optimizacionService';

const OptimizacionPage: React.FC = () => {
  const [resultado, setResultado] = useState<OptimizacionResponse | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string>('');

  const ejecutarOptimizacion = async () => {
    try {
      setLoading(true);
      setError('');
      const data = await optimizacionService.ejecutarOptimizacion();
      setResultado(data);
    } catch (error: any) {
      setError(error.response?.data?.message || 'Error al ejecutar la optimización');
    } finally {
      setLoading(false);
    }
  };

  const MetricCard: React.FC<{
    title: string;
    value: string;
    icon: React.ReactNode;
    color: string;
  }> = ({ title, value, icon, color }) => (
    <Card>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Box sx={{ color, mr: 1 }}>{icon}</Box>
          <Typography variant="h6" component="div">
            {title}
          </Typography>
        </Box>
        <Typography variant="h4" component="div" sx={{ fontWeight: 'bold', color }}>
          {value}
        </Typography>
      </CardContent>
    </Card>
  );

  return (
    <Box>
      {/* Header con información del motor de optimización */}
      <Card sx={{ mb: 3, bgcolor: '#f8f9fa' }}>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
            <CloudIcon sx={{ color: '#1976d2', mr: 1 }} />
            <Typography variant="h5" component="h1" sx={{ fontWeight: 'bold' }}>
              Optimización de Producción con IBM Watson Studio
            </Typography>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <PsychologyIcon sx={{ color: '#388e3c', mr: 1, fontSize: 20 }} />
            <Typography variant="body1" color="text.secondary">
              Motor de optimización: <strong>Modelo OPL (Optimization Programming Language)</strong>
            </Typography>
          </Box>
          <Typography variant="body2" color="text.secondary">
            La optimización se ejecuta en IBM Watson Studio utilizando programación lineal para maximizar 
            el beneficio neto considerando restricciones de capacidad, calidad y demanda.
          </Typography>
        </CardContent>
      </Card>

      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h2">
          Ejecutar Optimización
        </Typography>
        <Button
          variant="contained"
          startIcon={<PlayArrowIcon />}
          onClick={ejecutarOptimizacion}
          disabled={loading}
          size="large"
          sx={{ 
            bgcolor: '#1976d2',
            '&:hover': { bgcolor: '#1565c0' }
          }}
        >
          {loading ? 'Ejecutando en Watson Studio...' : 'Ejecutar Optimización'}
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {loading && (
        <Box sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
          <CircularProgress size={60} sx={{ mb: 2 }} />
          <Typography variant="h6" color="text.secondary">
            Ejecutando optimización en IBM Watson Studio...
          </Typography>
          <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
            Procesando modelo OPL con datos actuales
          </Typography>
        </Box>
      )}

      {resultado && (
        <Box>
          {/* Información del resultado */}
          <Card sx={{ mb: 3, bgcolor: '#e8f5e8' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <PsychologyIcon sx={{ color: '#388e3c', mr: 1 }} />
                <Typography variant="h6" color="#388e3c">
                  Optimización Completada Exitosamente
                </Typography>
              </Box>
              <Typography variant="body2" color="text.secondary">
                Resultados obtenidos del modelo OPL ejecutado en IBM Watson Studio
              </Typography>
            </CardContent>
          </Card>

          {/* Métricas principales */}
          <Grid container spacing={3} sx={{ mb: 4 }}>
            <Grid item xs={12} sm={6} md={3}>
              <MetricCard
                title="Ingreso Total"
                value={`$${resultado.metricas.ingresoTotal.toLocaleString()}`}
                icon={<MoneyIcon />}
                color="#2e7d32"
              />
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <MetricCard
                title="Costo Total"
                value={`$${resultado.metricas.costoTotal.toLocaleString()}`}
                icon={<MoneyIcon />}
                color="#d32f2f"
              />
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <MetricCard
                title="Beneficio Neto"
                value={`$${resultado.metricas.beneficioNeto.toLocaleString()}`}
                icon={<TrendingUpIcon />}
                color={resultado.metricas.beneficioNeto >= 0 ? '#1976d2' : '#f57c00'}
              />
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <MetricCard
                title="Excedentes"
                value={`${resultado.metricas.litrosExcedentes.toLocaleString()} L`}
                icon={<WarningIcon />}
                color="#ed6c02"
              />
            </Grid>
          </Grid>

          {/* Tabla de asignaciones */}
          <Card sx={{ mb: 4 }}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Asignaciones Óptimas de Aceites a Gasolinas
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                Resultado del modelo OPL: variables Blend[o][g]
              </Typography>
              <TableContainer component={Paper}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Aceite</TableCell>
                      <TableCell>Gasolina</TableCell>
                      <TableCell align="right">Litros Asignados</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {resultado.asignaciones.map((asignacion, index) => (
                      <TableRow key={index}>
                        <TableCell>{asignacion.aceiteNombre}</TableCell>
                        <TableCell>{asignacion.gasolinaNombre}</TableCell>
                        <TableCell align="right">
                          {asignacion.litros.toLocaleString()} L
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>

          {/* Tabla de producción */}
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Producción por Tipo de Gasolina
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                Incluye variables a[g] para producción adicional según el modelo OPL
              </Typography>
              <TableContainer component={Paper}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Gasolina</TableCell>
                      <TableCell align="right">Demanda (L)</TableCell>
                      <TableCell align="right">Producido (L)</TableCell>
                      <TableCell align="right">Excedente (L)</TableCell>
                      <TableCell align="right">Cumplimiento</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {resultado.produccion.map((prod) => (
                      <TableRow key={prod.gasolinaId}>
                        <TableCell>{prod.gasolinaNombre}</TableCell>
                        <TableCell align="right">{prod.demanda.toLocaleString()}</TableCell>
                        <TableCell align="right">{prod.litrosProducidos.toLocaleString()}</TableCell>
                        <TableCell align="right">
                          {prod.excedente > 0 ? (
                            <Chip
                              label={`${prod.excedente.toLocaleString()} L`}
                              color="warning"
                              size="small"
                            />
                          ) : (
                            '-'
                          )}
                        </TableCell>
                        <TableCell align="right">
                          <Chip
                            label={`${prod.porcentajeCumplimiento.toFixed(1)}%`}
                            color={prod.porcentajeCumplimiento >= 100 ? 'success' : 'warning'}
                            size="small"
                          />
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>
        </Box>
      )}
    </Box>
  );
};

export default OptimizacionPage;
