import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Button, 
  Typography, 
  Grid, 
  Card, 
  CardContent, 
  CardActions,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  LinearProgress,
  Alert,
  CircularProgress,
} from '@mui/material';
import {
  OilBarrel as OilBarrelIcon,
  LocalGasStation as GasStationIcon,
  Settings as SettingsIcon,
  TrendingUp as TrendingUpIcon,
  AttachMoney as MoneyIcon,
  Assessment as AssessmentIcon,
  Warning as WarningIcon,
  CheckCircle as CheckCircleIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { aceitesService } from '../services/aceitesService';
import { gasolinasService } from '../services/gasolinasService';
import { parametrosService } from '../services/parametrosService';

interface DashboardStats {
  totalAceites: number;
  totalGasolinas: number;
  capacidadTotal: number;
  demandaTotal: number;
  costoPromedioAceites: number;
  precioPromedioGasolinas: number;
  ultimaOptimizacion?: string;
  estadoSistema: 'optimizado' | 'pendiente' | 'error';
}

const Dashboard: React.FC = () => {
  const navigate = useNavigate();
  const [stats, setStats] = useState<DashboardStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    loadDashboardStats();
  }, []);

  const loadDashboardStats = async () => {
    try {
      setLoading(true);
      const [aceites, gasolinas] = await Promise.all([
        aceitesService.getAll(),
        gasolinasService.getAll()
      ]);

      const capacidadTotal = aceites.reduce((sum, aceite) => sum + aceite.capacidad, 0);
      const demandaTotal = gasolinas.reduce((sum, gasolina) => sum + gasolina.demanda, 0);
      const costoPromedioAceites = aceites.length > 0 
        ? aceites.reduce((sum, aceite) => sum + aceite.costo, 0) / aceites.length 
        : 0;
      const precioPromedioGasolinas = gasolinas.length > 0 
        ? gasolinas.reduce((sum, gasolina) => sum + gasolina.precio, 0) / gasolinas.length 
        : 0;

      setStats({
        totalAceites: aceites.length,
        totalGasolinas: gasolinas.length,
        capacidadTotal,
        demandaTotal,
        costoPromedioAceites,
        precioPromedioGasolinas,
        estadoSistema: capacidadTotal >= demandaTotal ? 'optimizado' : 'pendiente'
      });
    } catch (error) {
      setError('Error al cargar las estadísticas del dashboard');
    } finally {
      setLoading(false);
    }
  };

  const getEstadoColor = (estado: string) => {
    switch (estado) {
      case 'optimizado': return 'success';
      case 'pendiente': return 'warning';
      case 'error': return 'error';
      default: return 'default';
    }
  };

  const getEstadoIcon = (estado: string) => {
    switch (estado) {
      case 'optimizado': return <CheckCircleIcon />;
      case 'pendiente': return <WarningIcon />;
      case 'error': return <WarningIcon />;
      default: return <AssessmentIcon />;
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      {/* Header del Dashboard */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h3" component="h1" gutterBottom sx={{ fontWeight: 'bold', color: '#1976d2' }}>
          Panel de Control - EnergíaFina S.A.
        </Typography>
        <Typography variant="h6" color="text.secondary" gutterBottom>
          Sistema de Optimización de Mezclas de Gasolina
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {/* Estado del Sistema */}
      {stats && (
        <Card sx={{ mb: 4, backgroundColor: '#f8f9fa' }}>
          <CardContent>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
              {getEstadoIcon(stats.estadoSistema)}
              <Typography variant="h6" sx={{ ml: 1 }}>
                Estado del Sistema
              </Typography>
            </Box>
            <Chip 
              label={stats.estadoSistema === 'optimizado' ? 'Sistema Optimizado' : 'Requiere Optimización'}
              color={getEstadoColor(stats.estadoSistema) as any}
              variant="filled"
              sx={{ fontSize: '1rem', py: 1 }}
            />
            {stats.estadoSistema === 'pendiente' && (
              <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                La capacidad total de aceites ({stats.capacidadTotal.toLocaleString()} L) es menor que la demanda total ({stats.demandaTotal.toLocaleString()} L)
              </Typography>
            )}
          </CardContent>
        </Card>
      )}

      {/* Métricas Principales */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ height: '100%', backgroundColor: '#e3f2fd' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <OilBarrelIcon sx={{ color: '#1976d2', mr: 1 }} />
                <Typography variant="h6">Aceites Crudos</Typography>
              </Box>
              <Typography variant="h4" sx={{ fontWeight: 'bold', color: '#1976d2' }}>
                {stats?.totalAceites || 0}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Tipos disponibles
              </Typography>
              <Typography variant="h6" sx={{ mt: 1, fontWeight: 'bold' }}>
                {stats?.capacidadTotal.toLocaleString() || 0} L
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Capacidad total
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small" onClick={() => navigate('/aceites')}>
                Gestionar Aceites
              </Button>
            </CardActions>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ height: '100%', backgroundColor: '#e8f5e8' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <GasStationIcon sx={{ color: '#388e3c', mr: 1 }} />
                <Typography variant="h6">Tipos de Gasolina</Typography>
              </Box>
              <Typography variant="h4" sx={{ fontWeight: 'bold', color: '#388e3c' }}>
                {stats?.totalGasolinas || 0}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Tipos disponibles
              </Typography>
              <Typography variant="h6" sx={{ mt: 1, fontWeight: 'bold' }}>
                {stats?.demandaTotal.toLocaleString() || 0} L
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Demanda total
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small" onClick={() => navigate('/gasolinas')}>
                Gestionar Gasolinas
              </Button>
            </CardActions>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ height: '100%', backgroundColor: '#fff3e0' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <MoneyIcon sx={{ color: '#f57c00', mr: 1 }} />
                <Typography variant="h6">Costos Promedio</Typography>
              </Box>
              <Typography variant="h4" sx={{ fontWeight: 'bold', color: '#f57c00' }}>
                ${stats?.costoPromedioAceites.toFixed(2) || '0.00'}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Costo promedio aceites
              </Typography>
              <Typography variant="h6" sx={{ mt: 1, fontWeight: 'bold' }}>
                ${stats?.precioPromedioGasolinas.toFixed(2) || '0.00'}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Precio promedio gasolinas
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small" onClick={() => navigate('/parametros')}>
                Ver Parámetros
              </Button>
            </CardActions>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ height: '100%', backgroundColor: '#fce4ec' }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <TrendingUpIcon sx={{ color: '#c2185b', mr: 1 }} />
                <Typography variant="h6">Optimización</Typography>
              </Box>
              <Typography variant="h4" sx={{ fontWeight: 'bold', color: '#c2185b' }}>
                {stats?.ultimaOptimizacion ? '✓' : '!'}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {stats?.ultimaOptimizacion ? 'Última ejecución' : 'Pendiente'}
              </Typography>
              <Typography variant="h6" sx={{ mt: 1, fontWeight: 'bold' }}>
                {stats?.estadoSistema === 'optimizado' ? 'Listo' : 'Requerida'}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Estado actual
              </Typography>
            </CardContent>
            <CardActions>
              <Button size="small" onClick={() => navigate('/optimizacion')}>
                Ejecutar Optimización
              </Button>
            </CardActions>
          </Card>
        </Grid>
      </Grid>

      {/* Acciones Rápidas */}
      <Typography variant="h5" sx={{ mb: 3, fontWeight: 'bold', color: '#1976d2' }}>
        Acciones Rápidas
      </Typography>
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Button
            variant="contained"
            color="primary"
            startIcon={<OilBarrelIcon sx={{ fontSize: 30 }} />}
            onClick={() => navigate('/aceites')}
            sx={{ 
              minWidth: 200, 
              minHeight: 80, 
              fontSize: 18, 
              fontWeight: 'bold', 
              p: 2,
              borderRadius: 2
            }}
            fullWidth
          >
            Gestionar Aceites
          </Button>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Button
            variant="contained"
            color="success"
            startIcon={<GasStationIcon sx={{ fontSize: 30 }} />}
            onClick={() => navigate('/gasolinas')}
            sx={{ 
              minWidth: 200, 
              minHeight: 80, 
              fontSize: 18, 
              fontWeight: 'bold', 
              p: 2,
              borderRadius: 2
            }}
            fullWidth
          >
            Gestionar Gasolinas
          </Button>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Button
            variant="contained"
            color="secondary"
            startIcon={<SettingsIcon sx={{ fontSize: 30 }} />}
            onClick={() => navigate('/parametros')}
            sx={{ 
              minWidth: 200, 
              minHeight: 80, 
              fontSize: 18, 
              fontWeight: 'bold', 
              p: 2,
              borderRadius: 2
            }}
            fullWidth
          >
            Configurar Parámetros
          </Button>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Button
            variant="contained"
            color="warning"
            startIcon={<TrendingUpIcon sx={{ fontSize: 30 }} />}
            onClick={() => navigate('/optimizacion')}
            sx={{ 
              minWidth: 200, 
              minHeight: 80, 
              fontSize: 18, 
              fontWeight: 'bold', 
              p: 2,
              borderRadius: 2
            }}
            fullWidth
          >
            Ejecutar Optimización
          </Button>
        </Grid>
      </Grid>

      {/* Información del Sistema */}
      <Card sx={{ backgroundColor: '#fafafa' }}>
        <CardContent>
          <Typography variant="h6" gutterBottom sx={{ fontWeight: 'bold' }}>
            Información del Sistema
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <Typography variant="body2" color="text.secondary">
                <strong>Versión:</strong> 1.0.0
              </Typography>
              <Typography variant="body2" color="text.secondary">
                <strong>Última actualización:</strong> {new Date().toLocaleDateString()}
              </Typography>
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography variant="body2" color="text.secondary">
                <strong>Integración:</strong> IBM Watson Studio
              </Typography>
              <Typography variant="body2" color="text.secondary">
                <strong>Almacenamiento:</strong> IBM Cloud Object Storage
              </Typography>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
    </Box>
  );
};

export default Dashboard;
